mapper.update:
delete from role_menu


		and roleId = #{roleId}


		and menuId = #{menuId}

mapper.select:
SELECT parent_id AS parentId, menu_name AS menuName, pc_menu.ID, m.url, COALESCE ( icon, '-1' ) AS icon, pc_menu.sort FROM pc_menu inner join menu as m on m.id=menu_id WHERE menu_id IN ( SELECT DISTINCT menuid FROM role_menu WHERE 1 = 1 AND roleid IN
#{roleId}
) ORDER BY sort
