mapper.select:
SELECT to_char( ck.create_time, 'yyyy-MM' ) AS date, count (*) FROM collect_knowledge ck
and ck.user_id = #{userId}
-- 查询参数，年份 AND to_char( ck.create_time, 'yyyy' ) = #{paramsDate}
GROUP BY -- 按照月份分组， to_char( ck.create_time, 'yyyy-MM' )

mapper.delete:
DELETE FROM collect_knowledge WHERE 1 = 1
AND senses_id in
${kId}
