mapper.select:
SELECT to_char( ck.create_time, 'yyyy-MM' ) AS date, count (*) FROM knowledge_view ck
and ck.user_id = #{userId}
-- 查询参数，年份 AND to_char( ck.create_time, 'yyyy' ) = #{paramsDate}
GROUP BY -- 按照月份分组， to_char( ck.create_time, 'yyyy-MM' )

SELECT user_id as userId, COUNT ( 1 ) as count FROM knowledge_view ck
-- 查询参数，年份 to_char( ck.create_time, 'yyyy' ) = #{paramsDate}
GROUP BY user_id ORDER BY count DESC LIMIT 10

SELECT senses_name , COUNT ( 1 ) as count FROM knowledge_view ck
-- 查询参数，年份 to_char( ck.create_time, 'yyyy' ) = #{paramsDate}
GROUP BY senses_name ORDER BY count DESC LIMIT 10

mapper.delete:
DELETE FROM knowledge_view WHERE 1 = 1
AND senses_id in
${kId}

mapper.select:
SELECT
senses_id
FROM
knowledge_view
WHERE
1 = 1
AND user_id =#{userId}
AND study_date > #{startTime}
AND study_date < #{endTime}
